import React, { useState, useEffect } from 'react';
import PlacesAutocomplete from 'react-places-autocomplete';

function AddressAutocomplete({ setDeliveryAddress }) {
  const [address, setAddress] = useState('');

  const handleSelect = async (value) => {
    setAddress(value);
    setDeliveryAddress(value);
    // You can use Google Maps Geocoding API here to get detailed address components if needed
  };

  return (
    <PlacesAutocomplete
      value={address}
      onChange={setAddress}
      onSelect={handleSelect}
    >
      {({ getInputProps, suggestions, getSuggestionItemProps, loading }) => (
        <div>
          <input {...getInputProps({ placeholder: 'Delivery Address' })} />

          <div className="autocomplete-dropdown-container">
            {loading && <div>Loading...</div>}
            {suggestions.map((suggestion) => {
              const className = suggestion.active
                ? 'suggestion-item--active'
                : 'suggestion-item';
              // inline style for demonstration purpose
              const style = suggestion.active
                ? { backgroundColor: '#a8dadc', cursor: 'pointer' }
                : { backgroundColor: '#fff', cursor: 'pointer' };
              return (
                <div
                  {...getSuggestionItemProps(suggestion, {
                    className,
                    style,
                  })}
                >
                  <span>{suggestion.description}</span>
                </div>
              );
            })}
          </div>
        </div>
      )}
    </PlacesAutocomplete>
  );
}

export default AddressAutocomplete;
